const cacheMap = [];
class GLTFLoaderExt {
    static async loadModel(url, progressCallback, clone = false) {
        if (cacheMap[url] && cacheMap[url].done && cacheMap[url].asset)
            return cacheMap[url].asset.clone(true);
        if (cacheMap[url] && !cacheMap[url].done) {
            return await new Promise((resolve, reject) => {
                setTimeout(async () => {
                    resolve(await GLTFLoaderExt.loadModel(url, progressCallback));
                }, 10);
            });
        }
        cacheMap[url] = { done: false, asset: null };
        Cache.clear();
        const dracoLoader = new DRACOLoader();
        dracoLoader.setDecoderPath("https://s3.eu-central-1.amazonaws.com/cdn.sugartech/mottobucket/draco/");
        let loader = new GLTFLoader();
        loader.setDRACOLoader(dracoLoader);
        let gltf;
        try {
            gltf = await new Promise(async (resolve, reject) => {
                try {
                    let result = await loader.loadAsync(url, progressCallback);
                    resolve(result);
                }
                catch (error) {
                    reject(error);
                }
            });
        }
        catch (error) {
            if (error == null) {
                return null;
            }
            cacheMap[url] = undefined;
            throw error;
        }
        if (gltf != null) {
            gltf.scene.traverse((object) => {
                if (object.type != "Mesh")
                    return;
                object.castShadow = true;
            });
            cacheMap[url].asset = gltf.scene;
            cacheMap[url].done = true;
            return clone ? gltf.scene.clone() : gltf.scene;
        }
        cacheMap[url] = undefined;
        return undefined;
    }
}

